sp_widget.script:
if(input){
	data.answer = calc();
}

function calc() {
	var n0 = input.n0;
	var n1 = input.n1;
	var tax = 0.019;
	var amount = ((n0 + n1) * tax);
	return amount.toFixed(2);
}
