sp_widget.client_script:
function($scope) {
  /* widget controller */
  var c = this;


	c.adopt = function(pet, i){
		c.data.action = 'adopt';
		c.data.i = i;
		c.server.update().then(function(){
			c.data.action = undefined;
			c.data.i = undefined;
		});
		c.server.refresh();
	}
}

sp_widget.css:
.pet-container{
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(3, 1fr);

  grid-column-gap: 0.5vw;
