}

.panel-heading{
  text-align:center;
}

.btn{
  grid-column: 3 / end;
  grid-row: 3 / end;
}

#bio{
  grid-column: 3 / end;
  grid-row: 1 / 3;

  padding: 0;
  margin: 0;
}

sp_widget.script:
(function() {

	data.pets = [];

	var gr = new GlideRecord('x_393226_project2_pets');
	gr.query();

	while(gr.next()){
		data.pets.push({
			id: gr.sys_id.toString(),
			name: gr.name.toString(),
			bio: gr.bio.toString(),
			photo: gr.photo.getDisplayValue().toString(),
			adopted: gr.adopted.getDisplayValue()
		});
	}

	if(input) {
		if(input.action == 'adopt'){
			var grPet = new GlideRecord('x_393226_project2_pets');
			var petID = data.pets[input.i].id;
			grPet.get(petID);
			grPet.adopted = true;
			grPet.update();
		}
	}
})();
