sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	//id=kb_article&sys_id=d15de43187032100deddb882a2e3ec7d

	data.articles = [];

	var instanceName = gs.getProperty("instance_name");
	var articles = [];
	var links = [];
	var titles = [];
	var bodies = [];

	var knowledge = new GlideRecord('kb_knowledge');
	knowledge.addEncodedQuery('kb_category.labelSTARTSWITHnews');
	knowledge.query();

	for(var x = 0; x < 3; x++){
		if(knowledge.next()){
			var id = knowledge.sys_id.toString();
			articles.push(id);
			var link = 'https://'+instanceName+'.service-now.com/Hillsborough%20County?id=kb_article&sys_id='+id;
			links.push(link);
			var title = knowledge.short_description.toString();
			titles.push(title);
			var body = knowledge.text.substring(0, 100).replace(/<p>|<\/p>/g,'');
			bodies.push(body);
		}
	}
	/*while(knowledge.next()){
		var id = knowledge.sys_id.toString();
		articles.push(id);
		var link = 'https://'+instanceName+'.service-now.com/Hillsborough%20County?id=kb_article&sys_id='+id;
		links.push(link);
		var title = knowledge.short_description.toString();
		titles.push(title);
		var body = knowledge.text.substring(0, 150).replace(/<p>|<\/p>/g,'');
		bodies.push(body);
	}*/

	for(var i in articles){
		var gr = new GlideRecord('sys_attachment');
		gr.addQuery('table_sys_id', articles[i]);
		gr.query();

		while(gr.next()){
			var ga = new GlideSysAttachment();
			var image = "data:" + gr.getValue('content_type') + ";base64," + ga.getContentBase64(gr);
			data.articles.push({
				image: image,
				link: links[i],
				title: titles[i],
				text: bodies[i]
			});
		}
	}
})();
